        );
        
        $apiAddress = Setting::find('apiAddress');
        
        // Login to the API to get the session cookie.
        $url = $apiAddress->value . '/auth/login';
        $jsonData = json_encode([
            'email'    => 'prasad@scienceatlantic.ca',
            'password' => 'password'
        ]);            
        $ch = curl_init($url);
        curl_setopt($ch, CURLOPT_HEADER, true);
        curl_setopt($ch, CURLOPT_HTTPHEADER, [
            'Content-Type: application/json;charset=UTF-8',                                                                                
            'Content-Length: ' . strlen($jsonData)
        ]);
        curl_setopt($ch, CURLOPT_POST, true);
        curl_setopt($ch, CURLOPT_POSTFIELDS, $jsonData);
        curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
        $response = curl_exec($ch);
        
        preg_match_all('/^Set-Cookie:\s*([^;]*)/mi', $response, $matches);
        $cookies = [];
        foreach($matches[1] as $item) {
            parse_str($item, $cookie);
            $cookies = array_merge($cookies, $cookie);
        }
        
        foreach($inventory as $index => $f) {
            $data['state'] = 'PENDING_APPROVAL';
            $data['data'] = [];
            
            // Facility.
            $organization = DB::table('organizations')->where('name', trim($f['institution']))->first();
            $province = DB::table('provinces')->where('name', trim(substr($f['province'], 5)))->first();
            $data['data']['facility'] = [
                'name'           => $f['research'],
                'organizationId' => $organization ? $organization->id : 1,
                'provinceId'     => $province ? $province->id : 1,
                'city'           => $f['city'],
                'website'        => $f['url'],
                'description'    => $f['does'],
                'isPublic'       => true,
                'datePublished'  => $f['date_posted'],
                'dateUpdated'    => $f['date_updated']
            ];
            
            $data['data']['disciplines'] = [4];
            $data['data']['sectors'] = [1];
            
            // Primary contact.
            $name = explode(' ', $f['contact_name']);
            $data['data']['primaryContact'] = [
                'facilityId' => '',
                'firstName'  => count($name) == 3 ? $name[0] . ' ' . $name[1] : $name[0],
                'lastName'   => count($name) == 3 ? $name[2] : $name[1],
                'email'      => $f['email'],
                'telephone'  => $f['telephone'],
                'extension'  => null,
                'position'   => $f['position'],
                'website'    => null
            ];
            
            // Equipment.
            $data['data']['equipment'] = [];
            foreach($lab_list as $e) {
                if ($e['inventory_id'] == $f['inventory_id']) {
                    array_push($data['data']['equipment'], [
                        'facilityId'        => $f['inventory_id'],
                        'type'              => $e['lab'],
                        'manufacturer'      => null,
                        'model'             => null,
                        'purpose'           => $e['descr'],
                        'specifications'    => null,
                        'isPublic'          => true,
                        'hasExcessCapacity' => false,
                        'yearPurchased'     => null,
                        'yearManufactured'  => null,
                        'keywords'          => null
                    ]);
                }
            }
            
            // POST records as 'PENDING_APPROVAL'.
            $url = $apiAddress->value . '/facility-repository';
            $jsonData = json_encode($data);            
            $ch = curl_init($url);
            curl_setopt($ch, CURLOPT_HTTPHEADER, [
                'Content-Type: application/json;charset=UTF-8',                                                                                
                'Content-Length: ' . strlen($jsonData)
            ]);
            curl_setopt($ch, CURLOPT_POST, true);
            curl_setopt($ch, CURLOPT_POSTFIELDS, $jsonData);
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
            $fr = json_decode(curl_exec($ch), true);
            curl_close($ch);
            
            // POST records as 'PUBLISHED'.
            $queryParams = http_build_query([
                'state' => 'PUBLISHED',
                'reviewMessage' => 'Importing data from AFRED V1.'
            ]);
            $url = $apiAddress->value . '/facility-repository/' . $fr['id'] . '?' . $queryParams;

            $ch =  curl_init($url);
            curl_setopt($ch, CURLOPT_HTTPHEADER, [
                'Content-Type: application/json;charset=UTF-8',                                                                                
                'Content-Length: ' . strlen($jsonData),
                'Cookie: laravel_session=' . $cookies['laravel_session'],
            ]);
            curl_setopt($ch, CURLOPT_PUT, true);
            curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);
            $response = curl_exec($ch);
            curl_close($ch);
        }
